/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);
  --opaque-button-border-intensity: -8;
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);
  --background: 0 0% 100%;
  --foreground: 0 0% 10%;
  --border: 0 0% 90%;
  --card: 0 0% 98%;
  --card-foreground: 0 0% 10%;
  --card-border: 0 0% 94%;
  --sidebar: 0 0% 96%;
  --sidebar-foreground: 0 0% 10%;
  --sidebar-border: 0 0% 92%;
  --sidebar-primary: 262 83% 58%;
  --sidebar-primary-foreground: 0 0% 98%;
  --sidebar-accent: 262 15% 88%;
  --sidebar-accent-foreground: 0 0% 10%;
  --sidebar-ring: 262 83% 58%;
  --popover: 0 0% 94%;
  --popover-foreground: 0 0% 10%;
  --popover-border: 0 0% 90%;
  --primary: 262 83% 58%;
  --primary-foreground: 0 0% 98%;
  --secondary: 262 8% 88%;
  --secondary-foreground: 0 0% 10%;
  --muted: 262 6% 92%;
  --muted-foreground: 0 0% 40%;
  --accent: 262 12% 90%;
  --accent-foreground: 0 0% 10%;
  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 98%;
  --input: 0 0% 70%;
  --ring: 262 83% 58%;
  --chart-1: 262 70% 50%;
  --chart-2: 190 70% 45%;
  --chart-3: 340 75% 55%;
  --chart-4: 43 85% 55%;
  --chart-5: 160 60% 45%;
  --font-sans: Open Sans, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem;
  --shadow-2xs: 0px 2px 0px 0px hsl(262 83% 58% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(262 83% 58% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(262 83% 58% / 0.00), 0px 1px 2px -1px hsl(262 83% 58% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(262 83% 58% / 0.00), 0px 1px 2px -1px hsl(262 83% 58% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(262 83% 58% / 0.00), 0px 2px 4px -1px hsl(262 83% 58% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(262 83% 58% / 0.00), 0px 4px 6px -1px hsl(262 83% 58% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(262 83% 58% / 0.00), 0px 8px 10px -1px hsl(262 83% 58% / 0.00);
