  --destructive-foreground: 0 0% 98%;
  --input: 0 0% 30%;
  --ring: 262 80% 65%;
  --chart-1: 262 75% 70%;
  --chart-2: 190 75% 65%;
  --chart-3: 340 80% 70%;
  --chart-4: 43 90% 65%;
  --chart-5: 160 70% 60%;
  --shadow-2xs: 0px 2px 0px 0px hsl(262 80% 65% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(262 80% 65% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(262 80% 65% / 0.00), 0px 1px 2px -1px hsl(262 80% 65% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(262 80% 65% / 0.00), 0px 1px 2px -1px hsl(262 80% 65% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(262 80% 65% / 0.00), 0px 2px 4px -1px hsl(262 80% 65% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(262 80% 65% / 0.00), 0px 4px 6px -1px hsl(262 80% 65% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(262 80% 65% / 0.00), 0px 8px 10px -1px hsl(262 80% 65% / 0.00);
